mapper.select:



		TEAM_ID=#{teamId}


		"POSITION"=#{position}


		PLAYER_NAME LIKE '%' || #{playerName} || '%'
		OR BACK_NO LIKE '%' || #{backNo} ||
		'%'


ORDER BY TEAM_ID, BACK_NO, PLAYER_NAME

SELECT SCHE_DATE, STADIUM_NAME, t1.TEAM_NAME AS HOMETEAM_NAME, HOME_SCORE, t2.TEAM_NAME AS AWAYTEAM_NAME, AWAY_SCORE FROM SCHEDULE sc JOIN STADIUM st USING(STADIUM_ID) JOIN TEAM t1 ON sc.HOMETEAM_ID = t1.TEAM_ID JOIN TEAM t2 ON sc.AWAYTEAM_ID = t2.TEAM_ID
SCHE_DATE=#{scheDate}
t1.TEAM_NAME LIKE '%' || #{hometeamName} || '%' OR t2.TEAM_NAME LIKE '%' || #{awayteamName} || '%'

mapper.update:
UPDATE STADIUM 
	SET ZIP_CODE1=#{zipCode1}, ZIP_CODE2=#{zipCode2}, DDD=#{ddd}, TEL=#{tel}, FAX=#{fax}
		WHERE STADIUM_ID=#{stadiumId}

UPDATE SCHEDULE SET GUBUN=#{gubun}, HOME_SCORE=#{homeScore}, AWAY_SCORE=#{awayScore}
	WHERE STADIUM_ID=#{stadiumId}
		AND SCHE_DATE=#{scheDate}
