mapper.select:
SELECT SEQ, ID, TITLE, MDATE, REGDATE
	FROM CALRENDARBOARD c 
	WHERE ID=#{id}
		AND SUBSTR(MDATE,1,8) = #{yyyymmdd}

SELECT COUNT(*)
	FROM CALRENDARBOARD c
	WHERE ID=#{id}
		AND SUBSTR(MDATE,1,8) = #{yyyymmdd}

SELECT SEQ, ID, TITLE, CONTENT, MDATE, REGDATE
	FROM CALRENDARBOARD c 
	WHERE SEQ=#{seq}
		AND ID=#{id}
		AND SUBSTR(MDATE,1,8) = #{yyyymmdd}
